// Process points to extract unique documents
const points = $json.result.points || [];
const documentMap = new Map();

// Group points by file_id to get unique documents
points.forEach(point => {
  const metadata = point.payload || {};
  const file_id = metadata.file_id;

  if (file_id && !documentMap.has(file_id)) {
    documentMap.set(file_id, {
      file_id: file_id,
      fileName: metadata.fileName || 'Unknown',
      fileType: metadata.fileType || 'Unknown',
      uploadTime: metadata.uploadTime || new Date().toISOString(),
      chunkCount: 1
    });
  } else if (file_id && documentMap.has(file_id)) {
    // Increment chunk count for existing documents
    const existing = documentMap.get(file_id);
    existing.chunkCount += 1;
    documentMap.set(file_id, existing);
  }
});

// Convert map to array and sort by upload time (newest first)
const documents = Array.from(documentMap.values())
  .sort((a, b) => new Date(b.uploadTime) - new Date(a.uploadTime));

return {
  json: {
    documents: documents,
    totalDocuments: documents.length,
    totalChunks: points.length
  }
};

const chunkSize = 250;
const chunkOverlap = 50;
const text = $input.item.json.data.replace(/\n/, '');

const chunks = [];
let remainingText = text;

while (remainingText.length > 0) {
  let splitPoint;

  // Try splitting at paragraph level first
  splitPoint = remainingText.lastIndexOf("\n\n", chunkSize);

  // If no paragraph split, try splitting at sentence level
  if (splitPoint === -1) {
    splitPoint = remainingText.lastIndexOf(". ", chunkSize);
  }

  // If no sentence split, try splitting at word level
  if (splitPoint === -1) {
    splitPoint = remainingText.lastIndexOf(" ", chunkSize);
  }

  // If still no split point, force cut at chunkSize
  if (splitPoint === -1 || splitPoint < chunkSize * 0.5) {
    splitPoint = chunkSize; // Hard split if no good split point
  }

  // Extract chunk and adjust remaining text with overlap
  let chunk = remainingText.substring(0, splitPoint).trim();
  chunks.push(chunk);

  // Move the pointer forward while keeping the overlap
  remainingText = remainingText.substring(Math.max(0, splitPoint - chunkOverlap)).trim();

  // Break if remaining text is too small to form another chunk
  if (remainingText.length < chunkSize * 0.2) {
    chunks.push(remainingText);
    break;
  }
}

return { chunks };

