// Extract and format context from search results
const points = $json.result.points || [];
const userMessage = $('Chat Webhook').item.json.message;

if (points.length === 0) {
  return {
    json: {
      context: "",
      userMessage: userMessage,
      hasContext: false,
      documentsUsed: 0
    }
  };
}

// Extract text from points and create context
const contextParts = points.map(point => {
  const payload = point.payload || {};
  return payload.text || '';
}).filter(text => text.trim().length > 0);

const context = contextParts.join('\n\n---\n\n');

// Count unique documents used
const uniqueDocs = new Set(points.map(point => {
  const payload = point.payload || {};
  return payload.file_id;
}).filter(id => id));

return {
  json: {
    context: context,
    userMessage: userMessage,
    hasContext: context.trim().length > 0,
    documentsUsed: uniqueDocs.size
  }
};

