// Generate unique file ID from file content
const crypto = require('crypto');

const fileData = $binary.data.data;
const fileName = $binary.data.fileName || 'unnamed-file';
const fileType = $binary.data.mimeType || $binary.data.fileType || 'unknown';

// Create SHA256 hash of file content for unique ID
const hash = crypto.createHash('sha256');
hash.update(fileData);
const file_id = hash.digest('hex');

return {
  json: {
    file_id,
    fileName,
    fileType,
    size: fileData.length,
    uploadTime: new Date().toISOString()
  }
};

